dna = ''
def reverse( str ):
    res = ''
    for i in str:
        res = i + res
    return res

def compliment( str ):
    res = ''
    dict = {'A':'T', 'T':'A', 'G':'C', 'C':'G'}
    for i in str:
        res += dict[i]
    return res


